import { useRef, useState } from "react";
import "./App.css";

interface Todo {
  id: number;
  content: string;
}

function App() {
  const idRef = useRef(0); // number 타입 저장장
  const [todos, setTodos] = useState<Todo[]>([]);

  // useState는 초기값으로 전달한 인수의 타입에 따라 state 변수와 state 변화 함수의 타입을 자동으로 추론
  // const [text, setText] = useState<string>(); // 제네릭 함수 (초기값이 비어있다면, undefined 타입으로 추론되므로, 이 경우 타입 변수 설정 필요)
  const [text, setText] = useState("");

  // e는 아무런 타입을 설정하지 않으면 암묵적 any 타입으로 지정
  const onChangeInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    setText(e.target.value);
  };

  const onClickAdd = () => {
    setTodos([
      ...todos,
      {
        id: idRef.current++,
        content: text,
      },
    ]);

    setText("");
  };

  return (
    <div className="App">
      <h1> Todo </h1>
      <input value={text} onChange={onChangeInput} />
      <button> 추가 </button>
    </div>
  );
}

export default App;
